const Channels = ({ provider, account, dappcord, channels, currentChannel, setCurrentChannel }) => {

  const mintNFT = async () => {
    const minted = await dappcord.safeMint(account, "")
  }

  function ImageUploadForm() {
    const [image, setImage] = useState(null);

    const handleSubmit = (e) => {
      e.preventDefault();
    // handle the form submission here
      console.log(image);
  };

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="image-upload">Choose an image:</label>
      <input
        id="image-upload"
        type="file"
        accept="image/*"
        onChange={handleImageChange}
      />
      <button type="submit">Upload</button>
    </form>
  );
}


  return (
    <div className="fileupload">
      <h3>Upload a file to convert it to a Soul Token!</h3>
      <div className="spacer"></div>

      <button type="button" className="btn__upload"> Upload </button>
    </div>
  );
}

export default Channels;
